const answerButtons=document.querySelectorAll(".answer-button"),volumeBtn=document.querySelector(".volume-button"),audio1=new Audio("./audios/n.mp3"),audio2=new Audio("./audios/n.mp3"),audio3=new Audio("./audios/n.mp3"),audio4=new Audio("./audios/n.mp3"),sounds=[audio1,audio2,audio3,audio4];function initSoundOnHover(){answerButtons.forEach((o,n)=>{o.addEventListener("mouseover",()=>{sounds[n].play()})})}function onCLickVolumeIcon(){volumeBtn.classList.contains("active")?sounds.forEach(o=>{o.muted=!1}):sounds.forEach(o=>{o.muted=!0}),initSoundOnHover()}window.addEventListener("load",onCLickVolumeIcon),volumeBtn.addEventListener("click",()=>{volumeBtn.classList.toggle("active")});